$(document).ready(function () {
	/*$('.bp_thumbnail_resize').each(function(){
		$(this).attr({src:$(this).attr("src").replace(/\/s72(.*)\//g,"/w294-no/")})
	});*/

	var labelget = $('.slider4').text().trim();
	if(labelget != '')
		var urlfeed = '/feeds/posts/default/-/'+labelget;
	else
		var urlfeed = '/feeds/posts/default/-/Photo';
	
	let options = {
			"url":urlfeed,
			"dataSend":{
				"max-results": 20,
				"orderby":"updated"
			},
		};
	
	getAjax(options, function(data){
		if(data == "effFeed"){
			$('.slider4').html('<div class="slide"><strong>Error Loading Feed!</strong></div>');
		}else{
			var posturl, posttitle, postimg, sliderHtml = '',
				entry = data.feed.entry;
			if (entry !== undefined) {
				for (var i = 0; i < entry.length; i++) {
						for (var j=0; j < entry[i].link.length; j++)
						{
							if (entry[i].link[j].rel == "alternate")
								{
									posturl = entry[i].link[j].href;
									break;
								}
						}                
					posttitle = entry[i].title.$t;
					if ("media$thumbnail" in entry[i]) {
						postimg = imageHostFix(entry[i].media$thumbnail.url.replace('/s72-c/','/w300-h280-c/'));
					} else {
						postimg = "http://placehold.it/300x280&amp;text=No+Image"
					}
					sliderHtml += '<div class="slide"><a href="' + posturl + '"><img title="'+posttitle+'" src="' + postimg + '" />'+ (labelget.indexOf("Video") != -1 ? '<div class="play-icon fa fa-play fa-5"></div>' : '') +'</a></div>';
				}
				$('.slider4').html(sliderHtml);		
				$('.slider4').bxSlider({
					slideWidth: 300,
					minSlides: 2,
					maxSlides: 5,
					moveSlides: 1,
					slideMargin: 10,
					pager: false,
					auto: true,
					autoHover: true,
					captions: true,
					randomStart: true
				  });
			} else {
				$('.slider4').html('<div class="slide"><span>No result!</span></div>');
			}
		}
	})
});


