function runMasonry(){
		$(".blog-posts").masonry({
			itemSelector:".post-outer",
			isAnimated:!0,
			gutterWidth:0,
			columnWidth:0,
			stamp:".stamp",
			animationOptions:{duration:950,easing:"easeInOutCubic",queue:!1}
		}).imagesLoaded(function(){
			$(".blog-posts").masonry("reloadItems");
			$(".blog-posts").masonry("layout")
		})
}


var fetchData = function(postsRef, sectionElement) {
	postsRef.on('value', function(data) {
		if(data.exists())
			var count = data.val().viewCount || 0;
		else
			var count = 0;
	  
	  sectionElement.html(count);
	});	
};

$(document).ready(function(){
	runMasonry();
	var showDataMulti = function(){
		$(".post-info .view-count-info .text-count").each(function(){
			var idPost = $(this).parent().attr("name");
			var typePost = $(this).prevAll("#typepost").attr("name");
			var postsRef = database.ref('posts/' + typePost + "/" + idPost);
			fetchData(postsRef, $(this));
		});
	}; 

	showDataMulti();
});


