jQuery.ias({
	container : '.blog-posts',
	item: '.post-outer',
	pagination: '#blog-pager',
	next: '#blog-pager-older-link a',
	loader: '<div id="load-more-post"><img src="https://lh6.googleusercontent.com/-LPAcEmv3WSs/VL7JpA0nx-I/AAAAAAAAB6c/Lf_MuWKWndc/s64-no/Preloader_10.gif"/>Đang tải dữ liệu...</div>',
	triggerPageThreshold: 3,
	onLoadItems: function(newElements) {
		$(newElements).each(function(){
			/*var imgE = $(this).find('.bp_thumbnail_resize');
			if(imgE.length > 0)
				imgE.attr({src:imgE.attr("src").replace(/\/s72(.*)\//g,"/w294-no/")});*/
				
			var idPost = $(this).find('.post-info .view-count-info').attr("name");
			var typePost = $(this).find("a#typepost").attr("name");
			var postsRef = database.ref('posts/' + typePost + "/" + idPost);
			
			fetchData(postsRef, $(this).find('.post-info .view-count-info .text-count'));
		});
		
		var $newElems = $(newElements).css({ opacity: 0 });
		$newElems.imagesLoaded(function(){
		  $newElems.animate({ opacity: 1 });
		  $('.blog-posts').masonry( 'appended', $newElems, true );
		  $('.blog-posts').masonry('layout');
		});
	},
	onPageChange: function() {				
		$('.blog-posts').masonry('layout');
	},
});


jQuery.cachedScript = function( url, options ) {
  options = $.extend( options || {}, {
	dataType: "script",
	cache: true,
	url: url
  });
  return jQuery.ajax( options );
};	

var fetchPosts = function(postsRef, sectionElement, timeTop, oneLabel) {	
	postsRef.once('value', function(data){
		var htmlEmbed = '';
		if(data.val() != null){
			let count = 0;
			data.forEach(function(childSnapshot) {
				var dataChild = childSnapshot.val();
				if(dataChild.type == "photo")					
					htmlEmbed = '<div class="col2 col-xs-6"><div class="card-outline">'+ (timeTop != "W" ? ('<a class="rt01img card-thumbnail" href="'+ imageHostFix(dataChild.thumb.replace(/\/s[0-9](.*)\//g, "/w300-c/")) +'">' + dataChild.title + '</a>') : ('<img class="rt01img card-thumbnail" src="'+ imageHostFix(dataChild.thumb.replace(/\/s[0-9](.*)\//g, "/w300-c/")) +'" />'))+'<div class="card-content"><a href="'+ dataChild.url +'">' + dataChild.title + '</a></div><span class="view-count"><i aria-hidden="true" class="fa fa-eye"></i><span class="text-view-count">'+ (timeTop == "W" ? dataChild.viewCountW: (timeTop == "M" ? dataChild.viewCountM : dataChild.viewCount)) +'</span></span></div></div>' + htmlEmbed;
				else if (dataChild.type == "video")
					if(oneLabel == true)
						htmlEmbed = '<div class="col2 col-xs-6"><div class="card-outline">' + (timeTop != "W" ? ('<a class="rt01img card-thumbnail" href="'+ imageHostFix(dataChild.thumb.replace(/\/s[0-9](.*)\//g, "/w241-h161-c/")) +'">' + dataChild.title + '</a>') : ('<img class="rt01img card-thumbnail" src="'+ imageHostFix(dataChild.thumb.replace(/\/s[0-9](.*)\//g, "/w241-h161-c/")) +'" />')) + '<div class="card-content"><a href="'+ dataChild.url +'">' + dataChild.title + '</a></div><span class="view-count"><i aria-hidden="true" class="fa fa-eye"></i><span class="text-view-count">'+ (timeTop == "W" ? dataChild.viewCountW: (timeTop == "M" ? dataChild.viewCountM : dataChild.viewCount)) +'</span></span><span class="perma-link"><a href="'+ dataChild.url +'"><i class="fa fa-play-circle" aria-hidden="true"></i></a></span></div></div>' + htmlEmbed;
					else{						
						if(count == 0){
							if(timeTop != "W" && timeTop != "M")
								$('.top-video .area-highlight').html('<a href="'+ dataChild.url +'"><h2 class="color-hl">'+ dataChild.title +'</h2></a><p><img alt="'+ dataChild.title +'" src="'+ imageHostFix(dataChild.thumb.replace(/\/s[0-9](.*)\//g, "/w465-h350-c/")) +'"></p><span class="perma-link"><a href="'+ dataChild.url +'"><i aria-hidden="true" class="fa fa-play-circle"></i></a></span>');
						}else
							htmlEmbed = '<div class="col3 col-xs-6"><div class="card-outline">'+ (timeTop != "W" ? ('<a class="rt01img card-thumbnail" href="'+ imageHostFix(dataChild.thumb.replace(/\/s[0-9](.*)\//g, "/w241-h161-c/")) +'">' + dataChild.title + '</a>') : ('<img class="rt01img card-thumbnail" src="'+ imageHostFix(dataChild.thumb.replace(/\/s[0-9](.*)\//g, "/w241-h161-c/")) +'" />')) +'<div class="card-content"><a href="'+ dataChild.url +'">' + dataChild.title + '</a></div><span class="view-count"><i aria-hidden="true" class="fa fa-eye"></i><span class="text-view-count">'+ (timeTop == "W" ? dataChild.viewCountW: (timeTop == "M" ? dataChild.viewCountM : dataChild.viewCount)) +'</span></span><span class="perma-link"><a href="'+ dataChild.url +'"><i class="fa fa-play-circle" aria-hidden="true"></i></a></span></div></div>' + htmlEmbed;
						
						count++;
					}						
				else if (dataChild.type == "wallpaper")
					htmlEmbed = '<div class="col2">'+ (timeTop != "W" ? ('<a class="rt01img card-img" href="'+ imageHostFix(dataChild.thumb.replace(/\/s[0-9](.*)\//g, "/w250-h166-c/")) +'">' + dataChild.title + '</a>') : ('<img class="rt01img card-img" src="'+ imageHostFix(dataChild.thumb.replace(/\/s[0-9](.*)\//g, "/w250-h166-c/")) +'" />')) +'<span class="view-count"><i aria-hidden="true" class="fa fa-eye"></i><span class="text-view-count">'+ (timeTop == "W" ? dataChild.viewCountW: (timeTop == "M" ? dataChild.viewCountM : dataChild.viewCount)) +'</span></span><span class="perma-link"><a href="'+ dataChild.url +'"><i class="fa fa-download" aria-hidden="true"></i></a></span></div>' + htmlEmbed;
				else if (dataChild.type == "gif")
					htmlEmbed = '<div class="col2">'+ (timeTop != "W" ? ('<a class="rt01img card-img" href="'+ imageHostFix(dataChild.thumb.replace(/\/s[0-9](.*)\//g, "/w250-h166-c/")) +'">' + dataChild.title + '</a>') : ('<img class="rt01img card-img" src="'+ imageHostFix(dataChild.thumb.replace(/\/s[0-9](.*)\//g, "/w250-h166-c/")) +'" />')) +'<span class="view-count"><i aria-hidden="true" class="fa fa-eye"></i><span class="text-view-count">'+ (timeTop == "W" ? dataChild.viewCountW: (timeTop == "M" ? dataChild.viewCountM : dataChild.viewCount)) +'</span></span><span class="perma-link"><a href="'+ dataChild.url +'"><i class="fa fa-film" aria-hidden="true"></i></a></span></div>' + htmlEmbed;
				else if (dataChild.type == "comicfull"){
					if(count == 0){
						if(timeTop != "W" && timeTop != "M")
							$('.top-comic .area-highlight').html('<a href="'+ dataChild.url +'"><h2 class="color-hl">'+ dataChild.title +'</h2></a><p><img alt="'+ dataChild.title +'" src="'+ imageHostFix(dataChild.thumb.replace(/\/s[0-9](.*)\//gi, "/w350-no/")) +'"></p>');
					}else
						htmlEmbed = '<div class="col2 col-xs-6"><div class="card-outline">'+ (timeTop != "W" ? ('<a class="rt01img card-thumbnail" href="'+ imageHostFix(dataChild.thumb.replace(/\/s[0-9](.*)\//g, "/w180-h246-c/")) +'">' + dataChild.title + '</a>') : ('<img class="rt01img card-thumbnail" src="'+ imageHostFix(dataChild.thumb.replace(/\/s[0-9](.*)\//g, "/w180-h246-c/")) +'" />')) +'<div class="card-content"><a href="'+ dataChild.url +'">' + dataChild.title + '</a></div><span class="view-count"><i aria-hidden="true" class="fa fa-eye"></i><span class="text-view-count">'+ (timeTop == "W" ? dataChild.viewCountW: (timeTop == "M" ? dataChild.viewCountM : dataChild.viewCount)) +'</span></span></div></div>' + htmlEmbed;
					
					count++;
				}					
				
			});
		}	
		
		$(sectionElement).append(htmlEmbed);
	});
};